// DustmanShooter.cs
using System.Collections;
using System.Collections.Generic;
using UnityEngine;

// A boss identical to Dustman but after jumping it shoots instead of sucking.
public class DustmanShooter : Bosses
{
    private BossStateMachine _stateMachine;
    void Start()
    {
        _stateMachine = GetComponent<BossStateMachine>();

        _stateMachine.AddState("Idle", GetComponent<Idle>());
        _stateMachine.AddState("Jumping", GetComponent<Jumping>());
        _stateMachine.AddState("Sucking", GetComponent<Sucking>());
        _stateMachine.AddState("Shooting", GetComponent<Shooting>());

        // DustmanShooter will go to Shooting after Jumping
        _stateMachine.AddTransition("Jumping", "Shooting");

        _stateMachine.SetState("Idle");
    }
}

using UnityEngine;

public class SkullApproach : BossState
{
    public override void Enter() { Debug.Log("SkullApproach Enter"); }
    public override void Active()
    {
        // after a tick go to SpinAttack for demo purposes
        GetComponent<BossStateMachine>().SetState("SpinAttack");
    }
}

using UnityEngine;

public class SkullSpin : BossState
{
    public override void Enter() { Debug.Log("SkullSpin Enter"); }
    public override void Active()
    {
        GetComponent<BossStateMachine>().SetState("Roar");
    }
}

using System;

// Lightweight key type for boss states. Uses an internal string identifier so
// different bosses can define different state names without colliding.
[Serializable]
public struct StateKey : IEquatable<StateKey>
{
    public string Id;

    public StateKey(string id)
    {
        Id = id ?? string.Empty;
    }

    public bool Equals(StateKey other)
    {
        return string.Equals(Id, other.Id);
    }

    public override bool Equals(object obj)
    {
        return obj is StateKey other && Equals(other);
    }

    public override int GetHashCode()
    {
        return Id != null ? Id.GetHashCode() : 0;
    }

    public override string ToString()
    {
        return Id;
    }

    public static implicit operator StateKey(string id) => new StateKey(id);
    public static implicit operator string(StateKey key) => key.Id;
}
